(()=>{"use strict";let t=new class{async fetchData(t){let e={};try{const n=await fetch(`https://api.weatherapi.com/v1/current.json?key=accc068634d04769b09171233231010&q=${t}`,{mode:"cors"});if(!n.ok)throw new Error("Something went wrong.");const c=await n.json();e.location_name=c.location.name,e.local_time=c.location.localtime,e.temp_c=c.current.temp_c,e.temp_f=c.current.temp_f,e.feelslike_c=c.current.feelslike_c,e.feelslike_f=c.current.feelslike_f,e.humidity=c.current.humidity,e.condition=c.current.condition.text,e.wind_kph=c.current.wind_kph,e.wind_mph=c.current.wind_mph,e.wind_dir=c.current.wind_dir,e.is_day=c.current.is_day,e.last_updated=c.current.last_updated,e.uv=c.current.uv}catch(t){console.log(t)}return e}};const e={getLocationData:async e=>{(t=>{let e=document.getElementById("weather-info-container");e.innerHTML="",e.classList.contains("empty-container")&&e.classList.remove("empty-container"),e.classList.contains("populated-container")||e.classList.add("populated-container");let n=document.createElement("div"),c=document.createElement("div");n.textContent=t.temp_c,c.textContent=t.location_name,e.appendChild(n),e.appendChild(c),console.log(t)})(await t.fetchData(e))}};document.getElementById("submit-location-button").addEventListener("click",(()=>{let t=document.getElementById("location-input").value;""==t?document.getElementById("warning-message").textContent="Input field is empty. Please enter the location.":e.getLocationData(t)}))})();